package org.jugru.miniJUnit;import static org.jugru.miniJUnit.util.StringUtils.isEmpty;public class Result {    public Result(boolean success, String methodName) {        this.success = success;        this.methodName = methodName;    }    public Result(boolean success, String methodName, String errorMessage) {        this.success = success;        this.methodName = methodName;        this.errorMessage = errorMessage;    }    public static Result ofTrue(String methodName) {        return new Result(true, methodName);    }    public static Result ofFalse(String methodName, String errorMessage) {        return new Result(false, methodName, errorMessage);    }    private boolean success;    private String methodName;    private String errorMessage;    public boolean isSuccess() {        return success;    }    public String getMethodName() {        return methodName;    }    @Override    public String toString() {        return "Result{" +                "success=" + success +                ", methodName='" + methodName + '\'' +                toStringErrorMessageLine() +                '}';    }    private String toStringErrorMessageLine() {        if(isEmpty(this.errorMessage)) {            return "";        }        return ", errorMessage='" + this.errorMessage + '\'';    }}