package org.jugru.miniJUnitTest;import org.jugru.miniJUnit.Core;import org.jugru.miniJUnit.Result;import org.jugru.miniJUnit.annotations.Test;import org.jugru.miniJUnit.asserts.Assert;import java.util.List;public class CoreTest {    @Test    public void testDefaultPackage() {        List<Result> testResults = Core.runTests(Core.CURRENT_PACKAGE);        long success = testResults.stream().filter(Result::isSuccess).count();        long error = testResults.size() - success;        Assert.assertTrue(success == 1 && error == 2, "Должно быть 1 успешный тест и 2 с ошибкой");    }}